CREATE TABLE IF NOT EXISTS Hotel (
	id INTEGER PRIMARY KEY,
    name TEXT NOT NULL,
    phone TEXT NOT NULL,
    location TEXT NOT NULL,
    email TEXT,
    price REAL NOT NULL DEFAULT 100,
    web TEXT,
    description TEXT,
    host TEXT NOT NULL DEFAULT '123@qq.com',
    room_type TEXT NOT NULL DEFAULT 'Large',
    bathrooms TEXT NOT NULL DEFAULT 'Yes',
    bedrooms INTEGER NOT NULL   DEFAULT 2,
    FOREIGN KEY (host) REFERENCES User(username)
);

CREATE TABLE IF NOT EXISTS Hotel_img(
    hotel_id INTEGER NOT NULL,
    url TEXT NOT NULL,
    FOREIGN KEY (hotel_id) REFERENCES Hotel (id)
);

CREATE TABLE IF NOT EXISTS Booking (
    booking_id TEXT PRIMARY KEY,
    username TEXT NOT NULL,
    passport TEXT NOT NULL,
    booking_date TEXT NOT NULL,
    check_in_date TEXT NOT NULL,
    days INTEGER NOT NULL,
    price REAL NOT NULL,
    room_type TEXT NOT NULL,
    comment TEXT
);

CREATE TABLE IF NOT EXISTS Booking_hotel (
    booking_id TEXT NOT NULL,
    hotel_id INTEGER NOT NULL,
    FOREIGN KEY (booking_id) REFERENCES Booking(booking_id),
    FOREIGN KEY (hotel_id) REFERENCES Hotel (id)
);

CREATE TABLE IF NOT EXISTS User_booking (
    booking_id TEXT NOT NULL,
    username TEXT NOT NULL,
    FOREIGN KEY (booking_id) REFERENCES Booking(booking_id),
    FOREIGN KEY (username) REFERENCES User(username)
);


CREATE TABLE IF NOT EXISTS Comments(
    comment_id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,
    rating REAL,
    comment TEXT,
    user_id TEXT,
    hotel_id INTEGER,
    FOREIGN KEY (user_id) REFERENCES User(username),
    FOREIGN KEY (hotel_id) REFERENCES Hotel(id)
);

